Consulta.sql:
update seguimiento_expedientes set fecha_final=now()
,activo=false where id_expediente=$id_expediente
and activo=true;

select array_agg(grupo) as categoria,array_agg(cuenta)as data from( select caso as grupo, count(caso)as cuenta from (
select sub1.id_expediente, (case when aprobado=1 then 'APROBADO' when aprobado=2 then CASE WHEN COALESCE(sub3.id_expediente, '') != '' THEN 'EN REVISIN' ELSE 'DEVUELTO' END ELSE (case when coalesce(sub3.id_expediente,'') = '' then (case when sum=12 then 'EN GESTIN' ELSE 'ASIGNADO' END) else 'EN REVISIN' end) END) as caso from ( select I.id_expediente, (case when coalesce(ESTADO_FOLIO,0) = 0 then 0 else 1 end)+ (case when coalesce(PRED_ADQ,0) = 0 then 0 else 1 end)+ (case when coalesce(DOC_NUM,0) = 0 then 0 else 1 end)+ (case when coalesce(DOC_FECHA,now()) = now() then 0 else 1 end)+ (case when coalesce(K.NOM_PROP_MI,'') = '' then 0 else 1 end)+ (case when coalesce(K.TIPO_DOC_PROP,'') = '' then 0 else 1 end)+ (case when coalesce(K.NUM_DOC_PROP,'') = '' then 0 else 1 end)+ (case when coalesce(K.PORCENT_COPROP,0) = 0 then 0 else 1 end)+ (case when coalesce(L.NOM_ANTE_PROP,'') = '' then 0 else 1 end)+ (case when coalesce(L.TIPO_DOC_PROP,'') = '' then 0 else 1 end)+ (case when coalesce(L.NUM_DOC_PROP,'') = '' then 0 else 1 end)+ (case when coalesce(L.PORCENT_COPROP,0) = 0 then 0 else 1 end) AS sum from info5_juridicos I LEFT JOIN (select * from info21_juridicos where consecutivo=1) J on I.id_expediente=J.id_expediente LEFT JOIN (select * from info8_propietario_juridico where consecutivo=1) K on I.id_expediente=K.id_expediente LEFT JOIN (select * from info6_propietario_anterior_juridico where consecutivo=1) L on I.id_expediente=L.id_expediente )sub1 LEFT JOIN info13_control_calidad_juridico sub2 on sub1.id_expediente=sub2.id_expediente LEFT JOIN ( select distinct id_expediente from tareas.tareas where estado=1 and ruta=4 )sub3 on sub1.id_expediente=sub3.id_expediente
)sub1 group by caso)sub

select jsonb_agg(id_permiso)as permisos from( select p.id_permiso from usuario u inner join rol_permisos r on r.id_rol=u.usuario_rol inner join permisos p on r.id_permiso=p.id_permiso
where usuario_usuario=token union select id_permiso from usuario u natural join usr_permisos up where usuario_usuario=token
)As s

       insert into insert-dinamico(id_expediente,consecutivo)
        values ($id_expediente,coalesce((select max(consecutivo)+1 
		from insert-dinamico where id_expediente=$id_expediente),1)
	  	)returning id_expediente

       insert into insert-dinamico(id_expediente,consecutivo,usuario_prestamo, fecha_sol_usu_prestamo)
        values ($id_expediente,coalesce((select max(consecutivo)+1 
		from insert-dinamico where id_expediente=$id_expediente),1),token, now()
	  	)returning id_expediente

       insert into insert-dinamico(id_servidumbre,consecutivo)
        values ($id_servidumbre,coalesce((select max(consecutivo)+1 
		from insert-dinamico where id_servidumbre=$id_servidumbre),1)
	  	)returning id_servidumbre

select *
from dup
where id_proyecto = $proyecto;

select *
from servicios s
where s.id_formulario = $id_formulario;

select eval_validadores(va.id_formulario::integer,pr.id_expediente)
    from info2_general_predio pr, 
    (select distinct id_formulario from validadores) va
    order by pr.id_expediente, va.id_formulario;

select *    
from busqueda_avanzada_filtros
